mapper.select:
drop table if exists Duhe;
drop table if exists Duhe_mid;
create temp table Duhe as select st_setsrid(rast,4507) as rast  from (select rast as rast from geohazard_chain_raster where name='chain_dongchuan_dem') as t;
create temp table Duhe_mid as
select st_reclass(rast,1,'[686-4282]:1','8BUI',0) as rast from Duhe;
insert into geohazard_chain_Midrast(name ,rast) values ('Duhe_mid' ,(select rast from pg_temp.Duhe_mid));

drop table if exists ${table};
create temp table ${table} as
select st_setvalue(rast,1,geom,#{value}) as rast
from (select rast as rast from geohazard_chain_Midrast where name=#{prevname}) as t,(select wkb_geometry as geom from potential_landslide where 编码=#{id}) as riskpoint;
insert into geohazard_chain_Midrast(name ,rast) values (#{curname} ,(select rast from ${table}));

select st_astiff(rast) as tif
from geohazard_chain_Midrast
where name=#{name};
