mapper.select:
select id,
working_area_id,
in_num_classes,
out_num_classes,
create_time,
modify_time
from susceptibility.evaluation

    
        and id = #{id}
    
    
        and working_area_id = #{workingAreaId}
    
    
        and in_num_classes = #{inNumClasses}
    
    
        and out_num_classes = #{outNumClasses}
    

;
